$(document).ready(function(){$('a[href*="#"]:not([href="#"])').click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var a=$(this.hash);a=a.length?a:$("[name="+this.hash.slice(1)+"]");if(a.length){$("html, body").animate({scrollTop:a.offset().top},1000);return false}}});$("#faqs dd").hide();$("#faqs dt").click(function(){$(this).next("#faqs dd").slideToggle(500);$(this).toggleClass("expanded")});$.ajax({type:"GET",url:"https://api.github.com/repos/AntumDeluge/debreate/contributors",dataType:"json",success:function(a){$.each(a,function(b,c){$("#contributors").append('<div class="contributor card"><img src="'+a[b].avatar_url+'"><p><a href="'+a[b].html_url+'" class="contributor-login">'+a[b].login+"</a><br>"+a[b].contributions+" contribution"+((a[b].contributions>1)?"s":"")+"</p></div>")})}})});