$(document).ready(function(){$('a[href*="#"]:not([href="#"])').click(function(){if(location.pathname.replace(/^\//,"")==this.pathname.replace(/^\//,"")&&location.hostname==this.hostname){var b=$(this.hash);b=b.length?b:$("[name="+this.hash.slice(1)+"]");if(b.length){$("html, body").animate({scrollTop:b.offset().top},1000);return false}}});$.ajax({type:"GET",url:"https://api.github.com/repos/AntumDeluge/debreate",dataType:"json",success:function(b){$("#github-stars").append(b.stargazers_count);$("#github-forks").append(b.forks)}});$.ajax({type:"GET",url:"https://api.github.com/repos/AntumDeluge/debreate/contributors",dataType:"json",success:function(b){$.each(b,function(c,d){$("#contributors-list").append('<div class="contributor card"><img src="'+b[c].avatar_url+'"><p><a href="'+b[c].html_url+'" class="contributor-login">'+b[c].login+"</a><br>"+b[c].contributions+" contribution"+((b[c].contributions>1)?"s":"")+"</p></div>")})}});$("#faqs dd").hide();$("#faqs dt").click(function(){$(this).next("#faqs dd").slideToggle(500);$(this).toggleClass("expanded")});var a='<div class="modal-overlay js-modal-close"></div>';$("a[data-modal-id]").click(function(c){c.preventDefault();$("body").append(a);$(".modal-overlay").fadeTo(500,0.7);var b=$(this).attr("data-modal-id");$("#"+b).fadeIn($(this).data())});$(".js-modal-close, .modal-overlay").click(function(){$(".modal-box, .modal-overlay").fadeOut(500,function(){$(".modal-overlay").remove()})});$(window).resize(function(){$(".modal-box").css({top:($(window).height()-$(".modal-box").outerHeight())/2,left:($(window).width()-$(".modal-box").outerWidth())/2})});$(window).resize()});